import { useState } from "react";
import { useParams, Link } from "wouter";
import type { Product, Review, WishlistItem } from "@shared/schema";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import Header from "@/components/layout/header";
import Footer from "@/components/layout/footer";
import ProductGrid from "@/components/product/product-grid";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  Heart, 
  Star, 
  ShoppingCart, 
  Truck, 
  Shield, 
  RefreshCw, 
  Minus, 
  Plus,
  Share
} from "lucide-react";

export default function ProductDetail() {
  const { id } = useParams();
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [selectedSize, setSelectedSize] = useState("");
  const [selectedColor, setSelectedColor] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(0);
  const [reviewRating, setReviewRating] = useState(5);
  const [reviewComment, setReviewComment] = useState("");

  const { data: product, isLoading } = useQuery<Product>({
    queryKey: [`/api/products/${id}`],
    enabled: !!id,
  });

  const { data: reviews = [] } = useQuery<Review[]>({
    queryKey: [`/api/products/${id}/reviews`],
    enabled: !!id,
  });

  const { data: wishlistItems = [] } = useQuery<(WishlistItem & { product: Product })[]>({
    queryKey: ["/api/wishlist"],
    enabled: isAuthenticated,
  });

  const isInWishlist = wishlistItems.some((item: any) => item.productId === id);

  const addToCartMutation = useMutation({
    mutationFn: async () => {
      if (!selectedSize && product?.sizes && product.sizes.length > 0) {
        throw new Error("Please select a size");
      }
      if (!selectedColor && product?.colors && product.colors.length > 0) {
        throw new Error("Please select a color");
      }

      return apiRequest("POST", "/api/cart", {
        productId: id,
        quantity,
        size: selectedSize || null,
        color: selectedColor || null,
      });
    },
    onSuccess: () => {
      toast({
        title: "Added to Cart",
        description: "Product has been added to your cart successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const toggleWishlistMutation = useMutation({
    mutationFn: async () => {
      if (isInWishlist) {
        return apiRequest("DELETE", `/api/wishlist/${id}`);
      } else {
        return apiRequest("POST", "/api/wishlist", { productId: id });
      }
    },
    onSuccess: () => {
      toast({
        title: isInWishlist ? "Removed from Wishlist" : "Added to Wishlist",
        description: isInWishlist 
          ? "Product removed from your wishlist." 
          : "Product added to your wishlist.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/wishlist"] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update wishlist.",
        variant: "destructive",
      });
    },
  });

  const addReviewMutation = useMutation({
    mutationFn: async () => {
      return apiRequest("POST", `/api/products/${id}/reviews`, {
        rating: reviewRating,
        comment: reviewComment,
      });
    },
    onSuccess: () => {
      toast({
        title: "Review Added",
        description: "Thank you for your review!",
      });
      setReviewComment("");
      setReviewRating(5);
      queryClient.invalidateQueries({ queryKey: ["/api/products", id, "reviews"] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add review.",
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background">
        <Header />
        <div className="flex justify-center py-12">
          <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen bg-background">
        <Header />
        <div className="container mx-auto px-4 py-12 text-center">
          <h1 className="text-2xl font-bold text-foreground mb-4">Product Not Found</h1>
          <Link href="/products">
            <Button>Continue Shopping</Button>
          </Link>
        </div>
      </div>
    );
  }

  const images = product.images || [product.imageUrl];
  const currentPrice = product.salePrice ? parseFloat(product.salePrice) : parseFloat(product.price);
  const originalPrice = parseFloat(product.price);
  const discount = product.salePrice ? Math.round(((originalPrice - currentPrice) / originalPrice) * 100) : 0;

  return (
    <div className="min-h-screen bg-background">
      <Header />

      <div className="container mx-auto px-4 py-8">
        {/* Breadcrumb */}
        <nav className="mb-8">
          <div className="flex items-center space-x-2 text-sm text-muted-foreground">
            <Link href="/" className="hover:text-primary">Home</Link>
            <span>/</span>
            <Link href="/products" className="hover:text-primary">Products</Link>
            <span>/</span>
            <Link href={`/products?category=${product.category}`} className="hover:text-primary">
              {product.category}
            </Link>
            <span>/</span>
            <span className="text-foreground">{product.name}</span>
          </div>
        </nav>

        <div className="grid lg:grid-cols-2 gap-12">
          {/* Product Images */}
          <div className="space-y-4">
            <div className="aspect-square rounded-2xl overflow-hidden bg-secondary">
              <img
                src={images[selectedImage]}
                alt={product.name}
                className="w-full h-full object-cover"
                data-testid="img-product-main"
              />
            </div>
            {images.length > 1 && (
              <div className="grid grid-cols-4 gap-4">
                {images.map((image: string, index: number) => (
                  <button
                    key={index}
                    onClick={() => setSelectedImage(index)}
                    className={`aspect-square rounded-lg overflow-hidden border-2 transition-colors ${
                      selectedImage === index ? 'border-primary' : 'border-border'
                    }`}
                    data-testid={`button-image-${index}`}
                  >
                    <img
                      src={image}
                      alt={`${product.name} ${index + 1}`}
                      className="w-full h-full object-cover"
                    />
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Product Info */}
          <div className="space-y-6">
            <div>
              <p className="text-sm text-muted-foreground mb-2">{product.brand}</p>
              <h1 className="text-3xl font-bold text-foreground mb-4" data-testid="text-product-name">
                {product.name}
              </h1>

              <div className="flex items-center space-x-4 mb-4">
                <div className="flex items-center space-x-1">
                  <Star className="w-5 h-5 text-yellow-400 fill-current" />
                  <span className="font-medium">{product.rating || "0"}</span>
                  <span className="text-muted-foreground">({product.reviewCount || 0} reviews)</span>
                </div>
                {product.isFeatured && (
                  <Badge variant="secondary">Featured</Badge>
                )}
              </div>

              <div className="flex items-center space-x-4 mb-6">
                <span className="text-3xl font-bold text-foreground" data-testid="text-product-price">
                  â‚¹{currentPrice.toLocaleString()}
                </span>
                {discount > 0 && (
                  <>
                    <span className="text-lg text-muted-foreground line-through">
                      â‚¹{originalPrice.toLocaleString()}
                    </span>
                    <Badge variant="destructive">{discount}% OFF</Badge>
                  </>
                )}
              </div>

              <p className="text-muted-foreground leading-relaxed" data-testid="text-product-description">
                {product.description}
              </p>
            </div>

            {/* Size Selection */}
            {product.sizes && product.sizes.length > 0 && (
              <div>
                <h3 className="font-semibold text-foreground mb-3">Size</h3>
                <div className="grid grid-cols-6 gap-2">
                  {product.sizes.map((size: string) => (
                    <Button
                      key={size}
                      variant={selectedSize === size ? "default" : "outline"}
                      className="aspect-square"
                      onClick={() => setSelectedSize(size)}
                      data-testid={`button-size-${size}`}
                    >
                      {size}
                    </Button>
                  ))}
                </div>
              </div>
            )}

            {/* Color Selection */}
            {product.colors && product.colors.length > 0 && (
              <div>
                <h3 className="font-semibold text-foreground mb-3">Color</h3>
                <div className="flex flex-wrap gap-2">
                  {product.colors.map((color: string) => (
                    <Button
                      key={color}
                      variant={selectedColor === color ? "default" : "outline"}
                      onClick={() => setSelectedColor(color)}
                      data-testid={`button-color-${color}`}
                    >
                      {color}
                    </Button>
                  ))}
                </div>
              </div>
            )}

            {/* Quantity */}
            <div>
              <h3 className="font-semibold text-foreground mb-3">Quantity</h3>
              <div className="flex items-center space-x-3">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  disabled={quantity <= 1}
                  data-testid="button-quantity-decrease"
                >
                  <Minus className="w-4 h-4" />
                </Button>
                <span className="text-lg font-medium w-12 text-center" data-testid="text-quantity">
                  {quantity}
                </span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setQuantity(quantity + 1)}
                  data-testid="button-quantity-increase"
                >
                  <Plus className="w-4 h-4" />
                </Button>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="space-y-4">
              <div className="flex space-x-4">
                <Button
                  size="lg"
                  className="flex-1"
                  onClick={() => addToCartMutation.mutate()}
                  disabled={addToCartMutation.isPending}
                  data-testid="button-add-to-cart"
                >
                  <ShoppingCart className="w-5 h-5 mr-2" />
                  {addToCartMutation.isPending ? "Adding..." : "Add to Cart"}
                </Button>

                {isAuthenticated && (
                  <Button
                    variant="outline"
                    size="lg"
                    onClick={() => toggleWishlistMutation.mutate()}
                    disabled={toggleWishlistMutation.isPending}
                    data-testid="button-toggle-wishlist"
                  >
                    <Heart className={`w-5 h-5 ${isInWishlist ? 'fill-current text-destructive' : ''}`} />
                  </Button>
                )}

                <Button variant="outline" size="lg" data-testid="button-share">
                  <Share className="w-5 h-5" />
                </Button>
              </div>

              {!isAuthenticated && (
                <p className="text-sm text-muted-foreground text-center">
                  <a href="/api/login" className="text-primary hover:underline">Sign in</a> to add to wishlist
                </p>
              )}
            </div>

            {/* Features */}
            <div className="grid grid-cols-3 gap-4 pt-6">
              <div className="text-center">
                <Truck className="w-8 h-8 text-primary mx-auto mb-2" />
                <p className="text-sm font-medium">Free Delivery</p>
                <p className="text-xs text-muted-foreground">On orders above â‚¹999</p>
              </div>
              <div className="text-center">
                <RefreshCw className="w-8 h-8 text-primary mx-auto mb-2" />
                <p className="text-sm font-medium">Easy Returns</p>
                <p className="text-xs text-muted-foreground">30-day return policy</p>
              </div>
              <div className="text-center">
                <Shield className="w-8 h-8 text-primary mx-auto mb-2" />
                <p className="text-sm font-medium">Secure Payment</p>
                <p className="text-xs text-muted-foreground">100% secure checkout</p>
              </div>
            </div>
          </div>
        </div>

        {/* Product Details Tabs */}
        <div className="mt-16">
          <Tabs defaultValue="details" className="w-full">
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="details">Product Details</TabsTrigger>
              <TabsTrigger value="reviews">Reviews ({reviews.length})</TabsTrigger>
              <TabsTrigger value="shipping">Shipping & Returns</TabsTrigger>
            </TabsList>

            <TabsContent value="details" className="mt-8">
              <Card>
                <CardContent className="p-6">
                  <div className="space-y-4">
                    <div>
                      <h3 className="font-semibold text-foreground mb-2">Description</h3>
                      <p className="text-muted-foreground leading-relaxed">
                        {product.description}
                      </p>
                    </div>

                    <Separator />

                    <div className="grid md:grid-cols-2 gap-6">
                      <div>
                        <h4 className="font-medium text-foreground mb-3">Product Information</h4>
                        <dl className="space-y-2">
                          <div className="flex justify-between">
                            <dt className="text-muted-foreground">Brand:</dt>
                            <dd className="font-medium">{product.brand}</dd>
                          </div>
                          <div className="flex justify-between">
                            <dt className="text-muted-foreground">Category:</dt>
                            <dd className="font-medium">{product.category}</dd>
                          </div>
                          {product.subcategory && (
                            <div className="flex justify-between">
                              <dt className="text-muted-foreground">Type:</dt>
                              <dd className="font-medium">{product.subcategory}</dd>
                            </div>
                          )}
                          <div className="flex justify-between">
                            <dt className="text-muted-foreground">Stock:</dt>
                            <dd className="font-medium">{product.stock} available</dd>
                          </div>
                        </dl>
                      </div>

                      {((product.sizes && product.sizes.length > 0) || (product.colors && product.colors.length > 0)) && (
                        <div>
                          <h4 className="font-medium text-foreground mb-3">Available Options</h4>
                          {product.sizes && product.sizes.length > 0 && (
                            <div className="mb-3">
                              <p className="text-sm text-muted-foreground mb-1">Sizes:</p>
                              <p className="font-medium">{product.sizes?.join(", ")}</p>
                            </div>
                          )}
                          {product.colors && product.colors.length > 0 && (
                            <div>
                              <p className="text-sm text-muted-foreground mb-1">Colors:</p>
                              <p className="font-medium">{product.colors.join(", ")}</p>
                            </div>
                          )}
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="reviews" className="mt-8">
              <div className="space-y-6">
                {/* Add Review */}
                {isAuthenticated && (
                  <Card>
                    <CardContent className="p-6">
                      <h3 className="font-semibold text-foreground mb-4">Write a Review</h3>
                      <div className="space-y-4">
                        <div>
                          <label className="block text-sm font-medium text-foreground mb-2">Rating</label>
                          <Select value={reviewRating.toString()} onValueChange={(value) => setReviewRating(parseInt(value))}>
                            <SelectTrigger className="w-32" data-testid="select-review-rating">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              {[5, 4, 3, 2, 1].map(rating => (
                                <SelectItem key={rating} value={rating.toString()}>
                                  <div className="flex items-center space-x-1">
                                    {[...Array(rating)].map((_, i) => (
                                      <Star key={i} className="w-4 h-4 text-yellow-400 fill-current" />
                                    ))}
                                    <span className="ml-2">{rating} Star{rating !== 1 ? 's' : ''}</span>
                                  </div>
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                        </div>

                        <div>
                          <label className="block text-sm font-medium text-foreground mb-2">Comment</label>
                          <Textarea
                            placeholder="Share your experience with this product..."
                            value={reviewComment}
                            onChange={(e) => setReviewComment(e.target.value)}
                            data-testid="textarea-review-comment"
                          />
                        </div>

                        <Button 
                          onClick={() => addReviewMutation.mutate()}
                          disabled={addReviewMutation.isPending || !reviewComment.trim()}
                          data-testid="button-submit-review"
                        >
                          {addReviewMutation.isPending ? "Submitting..." : "Submit Review"}
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                )}

                {/* Reviews List */}
                <div className="space-y-4">
                  {reviews.length === 0 ? (
                    <Card>
                      <CardContent className="p-6 text-center">
                        <p className="text-muted-foreground">No reviews yet. Be the first to review this product!</p>
                      </CardContent>
                    </Card>
                  ) : (
                    reviews.map((review: any, index: number) => (
                      <Card key={review.id}>
                        <CardContent className="p-6">
                          <div className="flex items-start justify-between mb-3">
                            <div>
                              <div className="flex items-center space-x-2 mb-1">
                                <span className="font-medium text-foreground">
                                  {review.user?.firstName} {review.user?.lastName}
                                </span>
                                <div className="flex items-center space-x-1">
                                  {[...Array(review.rating)].map((_, i) => (
                                    <Star key={i} className="w-4 h-4 text-yellow-400 fill-current" />
                                  ))}
                                </div>
                              </div>
                              <p className="text-sm text-muted-foreground">
                                {new Date(review.createdAt).toLocaleDateString()}
                              </p>
                            </div>
                          </div>
                          <p className="text-foreground">{review.comment}</p>
                        </CardContent>
                      </Card>
                    ))
                  )}
                </div>
              </div>
            </TabsContent>

            <TabsContent value="shipping" className="mt-8">
              <Card>
                <CardContent className="p-6">
                  <div className="space-y-6">
                    <div>
                      <h3 className="font-semibold text-foreground mb-3">Shipping Information</h3>
                      <ul className="space-y-2 text-muted-foreground">
                        <li>â€¢ Free delivery on orders above â‚¹999</li>
                        <li>â€¢ Standard delivery: 2-3 business days</li>
                        <li>â€¢ Express delivery: Next business day (additional charges apply)</li>
                        <li>â€¢ We deliver Monday to Saturday (excluding holidays)</li>
                      </ul>
                    </div>

                    <Separator />

                    <div>
                      <h3 className="font-semibold text-foreground mb-3">Returns & Exchanges</h3>
                      <ul className="space-y-2 text-muted-foreground">
                        <li>â€¢ 30-day return policy from date of delivery</li>
                        <li>â€¢ Items must be in original condition with tags attached</li>
                        <li>â€¢ Free return pickup available</li>
                        <li>â€¢ Refunds processed within 5-7 business days</li>
                        <li>â€¢ Exchanges subject to product availability</li>
                      </ul>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>

        {/* Related Products */}
        <section className="mt-16">
          <h2 className="text-2xl font-bold text-foreground mb-8">You Might Also Like</h2>
          <ProductGrid category={product.category} excludeId={id} limit={4} />
        </section>
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/products.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import type { Product } from "@shared/schema";
import { useLocation } from "wouter";
import Header from "@/components/layout/header";
import Footer from "@/components/layout/footer";
import ProductGrid from "@/components/product/product-grid";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Slider } from "@/components/ui/slider";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import { Filter, Search, X, Grid, List } from "lucide-react";

export default function Products() {
  const [location, navigate] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [category, setCategory] = useState("");
  const [subcategory, setSubcategory] = useState("");
  const [sortBy, setSortBy] = useState("newest");
  const [priceRange, setPriceRange] = useState([0, 10000]);
  const [selectedSizes, setSelectedSizes] = useState<string[]>([]);
  const [selectedColors, setSelectedColors] = useState<string[]>([]);
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [showFilters, setShowFilters] = useState(false);

  // Parse URL parameters
  useEffect(() => {
    const params = new URLSearchParams(location.split('?')[1] || '');
    setSearchQuery(params.get('search') || '');
    setCategory(params.get('category') || '');
    setSubcategory(params.get('subcategory') || '');
    setSortBy(params.get('sort') || 'newest');
  }, [location]);

  const { data: products = [], isLoading } = useQuery<Product[]>({
    queryKey: ["/api/products", { category, search: searchQuery, limit: 50 }],
  });

  const categories = [
    { value: "", label: "All Categories" },
    { value: "men", label: "Men's Wear" },
    { value: "women", label: "Women's Wear" },
    { value: "kids", label: "Kids Wear" },
    { value: "accessories", label: "Accessories" },
  ];

  const subcategories = [
    { value: "", label: "All Items" },
    { value: "shirts", label: "Shirts" },
    { value: "pants", label: "Pants" },
    { value: "dresses", label: "Dresses" },
    { value: "shoes", label: "Shoes" },
    { value: "bags", label: "Bags" },
  ];

  const sizes = ["XS", "S", "M", "L", "XL", "XXL"];
  const colors = ["Black", "White", "Navy", "Gray", "Red", "Blue", "Green", "Pink"];

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    updateFilters({ search: searchQuery });
  };

  const updateFilters = (newFilters: Record<string, string>) => {
    const params = new URLSearchParams();
    Object.entries({ category, subcategory, search: searchQuery, sort: sortBy, ...newFilters })
      .forEach(([key, value]) => {
        if (value) params.set(key, value);
      });
    navigate(`/products?${params.toString()}`);
  };

  const clearFilters = () => {
    setSearchQuery("");
    setCategory("");
    setSubcategory("");
    setSortBy("newest");
    setPriceRange([0, 10000]);
    setSelectedSizes([]);
    setSelectedColors([]);
    navigate("/products");
  };

  const filteredProducts = products.filter((product: any) => {
    const matchesPrice = parseFloat(product.price) >= priceRange[0] && parseFloat(product.price) <= priceRange[1];
    const matchesSize = selectedSizes.length === 0 || selectedSizes.some(size => product.sizes?.includes(size));
    const matchesColor = selectedColors.length === 0 || selectedColors.some(color => product.colors?.includes(color));
    return matchesPrice && matchesSize && matchesColor;
  });

  const sortedProducts = [...filteredProducts].sort((a: any, b: any) => {
    switch (sortBy) {
      case "price_low":
        return parseFloat(a.price) - parseFloat(b.price);
      case "price_high":
        return parseFloat(b.price) - parseFloat(a.price);
      case "rating":
        return parseFloat(b.rating || "0") - parseFloat(a.rating || "0");
      case "newest":
      default:
        return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
    }
  });

  return (
    <div className="min-h-screen bg-background">
      <Header />

      <div className="container mx-auto px-4 py-8">
        {/* Search and Filter Bar */}
        <div className="mb-8">
          <div className="flex flex-col lg:flex-row gap-4 items-center justify-between">
            {/* Search */}
            <form onSubmit={handleSearch} className="flex-1 max-w-md">
              <div className="relative">
                <Input
                  type="text"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pr-10"
                  data-testid="input-search-products"
                />
                <Button
                  type="submit"
                  variant="ghost"
                  size="sm"
                  className="absolute right-2 top-1/2 transform -translate-y-1/2"
                  data-testid="button-search-products"
                >
                  <Search className="w-4 h-4" />
                </Button>
              </div>
            </form>

            {/* Controls */}
            <div className="flex items-center gap-4">
              <Button
                variant="outline"
                onClick={() => setShowFilters(!showFilters)}
                className="lg:hidden"
                data-testid="button-toggle-filters"
              >
                <Filter className="w-4 h-4 mr-2" />
                Filters
              </Button>

              <Select value={sortBy} onValueChange={(value) => { setSortBy(value); updateFilters({ sort: value }); }}>
                <SelectTrigger className="w-48" data-testid="select-sort">
                  <SelectValue placeholder="Sort by" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="newest">Newest First</SelectItem>
                  <SelectItem value="price_low">Price: Low to High</SelectItem>
                  <SelectItem value="price_high">Price: High to Low</SelectItem>
                  <SelectItem value="rating">Highest Rated</SelectItem>
                </SelectContent>
              </Select>

              <div className="flex border rounded-lg">
                <Button
                  variant={viewMode === "grid" ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setViewMode("grid")}
                  data-testid="button-grid-view"
                >
                  <Grid className="w-4 h-4" />
                </Button>
                <Button
                  variant={viewMode === "list" ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setViewMode("list")}
                  data-testid="button-list-view"
                >
                  <List className="w-4 h-4" />
                </Button>
              </div>
            </div>
          </div>

          {/* Active Filters */}
          {(category || subcategory || searchQuery || selectedSizes.length || selectedColors.length) && (
            <div className="flex flex-wrap gap-2 mt-4">
              {category && (
                <Badge variant="secondary" className="flex items-center gap-2">
                  Category: {categories.find(c => c.value === category)?.label}
                  <X className="w-3 h-3 cursor-pointer" onClick={() => { setCategory(""); updateFilters({ category: "" }); }} />
                </Badge>
              )}
              {subcategory && (
                <Badge variant="secondary" className="flex items-center gap-2">
                  Type: {subcategories.find(s => s.value === subcategory)?.label}
                  <X className="w-3 h-3 cursor-pointer" onClick={() => { setSubcategory(""); updateFilters({ subcategory: "" }); }} />
                </Badge>
              )}
              {searchQuery && (
                <Badge variant="secondary" className="flex items-center gap-2">
                  Search: "{searchQuery}"
                  <X className="w-3 h-3 cursor-pointer" onClick={() => { setSearchQuery(""); updateFilters({ search: "" }); }} />
                </Badge>
              )}
              {selectedSizes.map(size => (
                <Badge key={size} variant="secondary" className="flex items-center gap-2">
                  Size: {size}
                  <X className="w-3 h-3 cursor-pointer" onClick={() => setSelectedSizes(prev => prev.filter(s => s !== size))} />
                </Badge>
              ))}
              {selectedColors.map(color => (
                <Badge key={color} variant="secondary" className="flex items-center gap-2">
                  Color: {color}
                  <X className="w-3 h-3 cursor-pointer" onClick={() => setSelectedColors(prev => prev.filter(c => c !== color))} />
                </Badge>
              ))}
              <Button variant="ghost" size="sm" onClick={clearFilters} data-testid="button-clear-filters">
                Clear All
              </Button>
            </div>
          )}
        </div>

        <div className="flex gap-8">
          {/* Sidebar Filters */}
          <div className={`lg:block ${showFilters ? 'block' : 'hidden'} w-full lg:w-64 space-y-6`}>
            <Card>
              <CardContent className="p-6 space-y-6">
                <div>
                  <h3 className="font-semibold text-foreground mb-4">Category</h3>
                  <Select value={category} onValueChange={(value) => { setCategory(value); updateFilters({ category: value }); }}>
                    <SelectTrigger data-testid="select-category">
                      <SelectValue placeholder="Select category" />
                    </SelectTrigger>
                    <SelectContent>
                      {categories.map(cat => (
                        <SelectItem key={cat.value} value={cat.value}>{cat.label}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <h3 className="font-semibold text-foreground mb-4">Type</h3>
                  <Select value={subcategory} onValueChange={(value) => { setSubcategory(value); updateFilters({ subcategory: value }); }}>
                    <SelectTrigger data-testid="select-subcategory">
                      <SelectValue placeholder="Select type" />
                    </SelectTrigger>
                    <SelectContent>
                      {subcategories.map(subcat => (
                        <SelectItem key={subcat.value} value={subcat.value}>{subcat.label}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <h3 className="font-semibold text-foreground mb-4">Price Range</h3>
                  <div className="space-y-3">
                    <Slider
                      value={priceRange}
                      onValueChange={setPriceRange}
                      max={10000}
                      step={100}
                      className="w-full"
                      data-testid="slider-price-range"
                    />
                    <div className="flex justify-between text-sm text-muted-foreground">
                      <span>â‚¹{priceRange[0]}</span>
                      <span>â‚¹{priceRange[1]}</span>
                    </div>
                  </div>
                </div>

                <div>
                  <h3 className="font-semibold text-foreground mb-4">Size</h3>
                  <div className="grid grid-cols-3 gap-2">
                    {sizes.map(size => (
                      <div key={size} className="flex items-center space-x-2">
                        <Checkbox
                          id={`size-${size}`}
                          checked={selectedSizes.includes(size)}
                          onCheckedChange={(checked) => {
                            if (checked) {
                              setSelectedSizes(prev => [...prev, size]);
                            } else {
                              setSelectedSizes(prev => prev.filter(s => s !== size));
                            }
                          }}
                          data-testid={`checkbox-size-${size}`}
                        />
                        <Label htmlFor={`size-${size}`} className="text-sm">{size}</Label>
                      </div>
                    ))}
                  </div>
                </div>

                <div>
                  <h3 className="font-semibold text-foreground mb-4">Color</h3>
                  <div className="space-y-2">
                    {colors.map(color => (
                      <div key={color} className="flex items-center space-x-2">
                        <Checkbox
                          id={`color-${color}`}
                          checked={selectedColors.includes(color)}
                          onCheckedChange={(checked) => {
                            if (checked) {
                              setSelectedColors(prev => [...prev, color]);
                            } else {
                              setSelectedColors(prev => prev.filter(c => c !== color));
                            }
                          }}
                          data-testid={`checkbox-color-${color}`}
                        />
                        <Label htmlFor={`color-${color}`} className="text-sm">{color}</Label>
                      </div>
                    ))}
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Products Grid */}
          <div className="flex-1">
            <div className="flex items-center justify-between mb-6">
              <p className="text-muted-foreground">
                Showing {sortedProducts.length} of {products.length} products
              </p>
            </div>

            {isLoading ? (
              <div className="flex justify-center py-12">
                <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
              </div>
            ) : sortedProducts.length === 0 ? (
              <div className="text-center py-12">
                <p className="text-lg text-muted-foreground mb-4">No products found</p>
                <Button onClick={clearFilters} data-testid="button-clear-filters-empty">
                  Clear Filters
                </Button>
              </div>
            ) : (
              <ProductGrid products={sortedProducts} viewMode={viewMode} />
            )}
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/track-order.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import Header from "@/components/layout/header";
import Footer from "@/components/layout/footer";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  Package, 
  Truck, 
  CheckCircle, 
  Clock, 
  MapPin, 
  Search,
  AlertCircle
} from "lucide-react";

export default function TrackOrder() {
  const [location] = useLocation();
  const [orderNumber, setOrderNumber] = useState("");
  const [searchAttempted, setSearchAttempted] = useState(false);

  // Parse URL parameters
  useState(() => {
    const params = new URLSearchParams(location.split('?')[1] || '');
    const orderParam = params.get('order');
    if (orderParam) {
      setOrderNumber(orderParam);
      setSearchAttempted(true);
    }
  });

  const { data: order, isLoading, error } = useQuery({
    queryKey: ["/api/track", orderNumber],
    enabled: !!orderNumber && searchAttempted,
  });

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (orderNumber.trim()) {
      setSearchAttempted(true);
    }
  };

  const getStatusIcon = (status: string, isActive: boolean) => {
    const iconClass = `w-6 h-6 ${isActive ? 'text-primary' : 'text-muted-foreground'}`;

    switch (status) {
      case "pending":
      case "confirmed":
        return <Clock className={iconClass} />;
      case "shipped":
        return <Package className={iconClass} />;
      case "out_for_delivery":
        return <Truck className={iconClass} />;
      case "delivered":
        return <CheckCircle className={iconClass} />;
      default:
        return <Clock className={iconClass} />;
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case "pending":
        return "Order Placed";
      case "confirmed":
        return "Order Confirmed";
      case "shipped":
        return "Shipped";
      case "out_for_delivery":
        return "Out for Delivery";
      case "delivered":
        return "Delivered";
      default:
        return status;
    }
  };

  const trackingSteps = [
    { status: "pending", label: "Order Placed" },
    { status: "confirmed", label: "Order Confirmed" },
    { status: "shipped", label: "Shipped" },
    { status: "out_for_delivery", label: "Out for Delivery" },
    { status: "delivered", label: "Delivered" },
  ];

  const getCurrentStepIndex = (currentStatus: string) => {
    return trackingSteps.findIndex(step => step.status === currentStatus);
  };

  return (
    <div className="min-h-screen bg-background">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <div className="text-center mb-12">
            <h1 className="text-3xl font-bold text-foreground mb-4">Track Your Order</h1>
            <p className="text-lg text-muted-foreground">
              Enter your order number to get real-time tracking information
            </p>
          </div>

          {/* Search Form */}
          <Card className="mb-8">
            <CardContent className="p-6">
              <form onSubmit={handleSearch} className="flex gap-4">
                <Input
                  type="text"
                  placeholder="Enter order number (e.g., ORD-1234567890)"
                  value={orderNumber}
                  onChange={(e) => setOrderNumber(e.target.value)}
                  className="flex-1"
                  data-testid="input-order-number"
                />
                <Button type="submit" disabled={!orderNumber.trim()} data-testid="button-track-order">
                  <Search className="w-4 h-4 mr-2" />
                  Track Order
                </Button>
              </form>
            </CardContent>
          </Card>

          {/* Loading State */}
          {isLoading && (
            <Card>
              <CardContent className="p-12 text-center">
                <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4" />
                <p className="text-muted-foreground">Searching for your order...</p>
              </CardContent>
            </Card>
          )}

          {/* Error State */}
          {error && searchAttempted && (
            <Card>
              <CardContent className="p-12 text-center">
                <AlertCircle className="w-16 h-16 text-destructive mx-auto mb-4" />
                <h3 className="text-lg font-semibold text-foreground mb-2">Order Not Found</h3>
                <p className="text-muted-foreground mb-4">
                  We couldn't find an order with the number "{orderNumber}". 
                  Please check the order number and try again.
                </p>
                <p className="text-sm text-muted-foreground">
                  Order numbers usually start with "ORD-" followed by numbers and letters.
                </p>
              </CardContent>
            </Card>
          )}

          {/* Order Found */}
          {order && !isLoading && (
            <div className="space-y-8">
              {/* Order Header */}
              <Card>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <div>
                      <CardTitle className="text-2xl">Order #{order.orderNumber}</CardTitle>
                      <p className="text-muted-foreground">
                        Placed on {new Date(order.createdAt).toLocaleDateString()}
                      </p>
                    </div>
                    <Badge 
                      variant="secondary"
                      className="text-lg px-4 py-2"
                      data-testid="text-order-status"
                    >
                      {getStatusLabel(order.status)}
                    </Badge>
                  </div>
                </CardHeader>
              </Card>

              {/* Tracking Timeline */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <Package className="w-5 h-5" />
                    <span>Tracking Information</span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-6">
                    {/* Progress Steps */}
                    <div className="flex justify-between relative">
                      <div className="absolute top-6 left-6 right-6 h-0.5 bg-border" />
                      <div 
                        className="absolute top-6 left-6 h-0.5 bg-primary transition-all duration-500"
                        style={{ 
                          width: `${(getCurrentStepIndex(order.status) / (trackingSteps.length - 1)) * 100}%` 
                        }}
                      />

                      {trackingSteps.map((step, index) => {
                        const isActive = getCurrentStepIndex(order.status) >= index;
                        const isCurrent = order.status === step.status;

                        return (
                          <div key={step.status} className="flex flex-col items-center relative z-10">
                            <div 
                              className={`w-12 h-12 rounded-full border-2 flex items-center justify-center transition-colors ${
                                isActive 
                                  ? 'bg-primary border-primary text-primary-foreground' 
                                  : 'bg-background border-border'
                              }`}
                              data-testid={`step-${step.status}`}
                            >
                              {getStatusIcon(step.status, isActive)}
                            </div>
                            <p className={`mt-2 text-sm font-medium ${isActive ? 'text-foreground' : 'text-muted-foreground'}`}>
                              {step.label}
                            </p>
                            {isCurrent && (
                              <p className="text-xs text-primary font-medium mt-1">Current</p>
                            )}
                          </div>
                        );
                      })}
                    </div>

                    <Separator />

                    {/* Tracking Events */}
                    {order.tracking && order.tracking.length > 0 && (
                      <div>
                        <h3 className="font-semibold text-foreground mb-4">Tracking History</h3>
                        <div className="space-y-4">
                          {order.tracking.map((event: any, index: number) => (
                            <div key={event.id} className="flex space-x-4" data-testid={`tracking-event-${index}`}>
                              <div className="flex-shrink-0">
                                <div className="w-8 h-8 bg-primary/10 rounded-full flex items-center justify-center">
                                  {getStatusIcon(event.status, true)}
                                </div>
                              </div>
                              <div className="flex-1">
                                <div className="flex items-center justify-between">
                                  <h4 className="font-medium text-foreground">
                                    {getStatusLabel(event.status)}
                                  </h4>
                                  <p className="text-sm text-muted-foreground">
                                    {new Date(event.timestamp).toLocaleString()}
                                  </p>
                                </div>
                                {event.message && (
                                  <p className="text-sm text-muted-foreground mt-1">{event.message}</p>
                                )}
                                {event.location && (
                                  <div className="flex items-center space-x-1 mt-1">
                                    <MapPin className="w-3 h-3 text-muted-foreground" />
                                    <p className="text-xs text-muted-foreground">{event.location}</p>
                                  </div>
                                )}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>

              {/* Order Details */}
              <Card>
                <CardHeader>
                  <CardTitle>Order Details</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid md:grid-cols-2 gap-8">
                    {/* Shipping Address */}
                    <div>
                      <h3 className="font-semibold text-foreground mb-3">Shipping Address</h3>
                      <div className="text-sm text-muted-foreground space-y-1">
                        <p>{order.shippingAddress?.firstName} {order.shippingAddress?.lastName}</p>
                        <p>{order.shippingAddress?.address}</p>
                        <p>{order.shippingAddress?.city}, {order.shippingAddress?.state}</p>
                        <p>{order.shippingAddress?.pincode}</p>
                        <p>{order.shippingAddress?.phone}</p>
                      </div>
                    </div>

                    {/* Order Summary */}
                    <div>
                      <h3 className="font-semibold text-foreground mb-3">Order Summary</h3>
                      <div className="space-y-2 text-sm">
                        <div className="flex justify-between">
                          <span className="text-muted-foreground">Payment Method:</span>
                          <span className="capitalize">{order.paymentMethod}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-muted-foreground">Total Amount:</span>
                          <span className="font-medium">â‚¹{parseFloat(order.totalAmount).toLocaleString()}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-muted-foreground">Payment Status:</span>
                          <Badge variant={order.paymentStatus === 'paid' ? 'default' : 'secondary'}>
                            {order.paymentStatus}
                          </Badge>
                        </div>
                        {order.estimatedDelivery && (
                          <div className="flex justify-between">
                            <span className="text-muted-foreground">Expected Delivery:</span>
                            <span>{new Date(order.estimatedDelivery).toLocaleDateString()}</span>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>

                  <Separator className="my-6" />

                  {/* Order Items */}
                  <div>
                    <h3 className="font-semibold text-foreground mb-4">
                      Items ({order.orderItems?.length || 0})
                    </h3>
                    <div className="space-y-4">
                      {order.orderItems?.map((item: any, index: number) => (
                        <div key={item.id} className="flex space-x-4" data-testid={`order-item-${index}`}>
                          <img
                            src={item.product.imageUrl}
                            alt={item.product.name}
                            className="w-16 h-16 object-cover rounded-lg"
                          />
                          <div className="flex-1">
                            <h4 className="font-medium text-foreground">{item.product.name}</h4>
                            <p className="text-sm text-muted-foreground">{item.product.brand}</p>
                            <div className="flex items-center space-x-4 mt-1">
                              {item.size && (
                                <span className="text-xs bg-secondary px-2 py-1 rounded">
                                  Size: {item.size}
                                </span>
                              )}
                              {item.color && (
                                <span className="text-xs bg-secondary px-2 py-1 rounded">
                                  Color: {item.color}
                                </span>
                              )}
                              <span className="text-sm text-muted-foreground">Qty: {item.quantity}</span>
                              <span className="font-medium">â‚¹{parseFloat(item.price).toLocaleString()}</span>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>
          )}

          {/* Help Section */}
          <Card className="mt-8">
            <CardContent className="p-6">
              <div className="text-center">
                <h3 className="font-semibold text-foreground mb-2">Need Help?</h3>
                <p className="text-muted-foreground mb-4">
                  If you have any questions about your order, feel free to contact our support team.
                </p>
                <div className="flex justify-center space-x-4">
                  <Button variant="outline">Contact Support</Button>
                  <Button variant="outline">Track via SMS</Button>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>

      <Footer />
    </div>
  );
}

