import { Link } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Sheet, SheetContent, SheetHeader, SheetTitle } from "@/components/ui/sheet";
import { ShoppingBag, Minus, Plus, Trash2, ArrowRight } from "lucide-react";

interface CartSidebarProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export default function CartSidebar({ open, onOpenChange }: CartSidebarProps) {
  const { isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: cartItems = [], isLoading } = useQuery({
    queryKey: ["/api/cart"],
    enabled: isAuthenticated,
  });

  const updateQuantityMutation = useMutation({
    mutationFn: async ({ id, quantity }: { id: string, quantity: number }) => {
      return apiRequest("PUT", `/api/cart/${id}`, { quantity });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update quantity.",
        variant: "destructive",
      });
    },
  });

  const removeItemMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/cart/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "Item Removed",
        description: "Item has been removed from your cart.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to remove item.",
        variant: "destructive",
      });
    },
  });

  if (!isAuthenticated) {
    return (
      <Sheet open={open} onOpenChange={onOpenChange}>
        <SheetContent className="w-full max-w-md">
          <SheetHeader>
            <SheetTitle className="flex items-center space-x-2">
              <ShoppingBag className="w-5 h-5" />
              <span>Shopping Cart</span>
            </SheetTitle>
          </SheetHeader>
          <div className="flex flex-col items-center justify-center h-full">
            <ShoppingBag className="w-16 h-16 text-muted-foreground mb-4" />
            <h3 className="text-lg font-semibold text-foreground mb-2">Sign in to view cart</h3>
            <p className="text-muted-foreground text-center mb-6">
              Please sign in to add items to your cart and checkout.
            </p>
            <Button onClick={() => window.location.href = "/api/login"} data-testid="button-sign-in-cart">
              Sign In
            </Button>
          </div>
        </SheetContent>
      </Sheet>
    );
  }

  const subtotal = cartItems.reduce((sum: number, item: any) => {
    const price = item.product.salePrice ? parseFloat(item.product.salePrice) : parseFloat(item.product.price);
    return sum + (price * item.quantity);
  }, 0);

  const shipping = subtotal >= 999 ? 0 : 99;
  const total = subtotal + shipping;

  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent className="w-full max-w-md flex flex-col">
        <SheetHeader>
          <SheetTitle className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <ShoppingBag className="w-5 h-5" />
              <span>Shopping Cart</span>
            </div>
            <Badge variant="secondary">{cartItems.length} items</Badge>
          </SheetTitle>
        </SheetHeader>

        {isLoading ? (
          <div className="flex justify-center py-8">
            <div className="animate-spin w-6 h-6 border-4 border-primary border-t-transparent rounded-full" />
          </div>
        ) : cartItems.length === 0 ? (
          <div className="flex flex-col items-center justify-center flex-1">
            <ShoppingBag className="w-16 h-16 text-muted-foreground mb-4" />
            <h3 className="text-lg font-semibold text-foreground mb-2">Your cart is empty</h3>
            <p className="text-muted-foreground text-center mb-6">
              Add some items to your cart to get started.
            </p>
            <Button onClick={() => onOpenChange(false)} data-testid="button-continue-shopping-empty">
              Continue Shopping
            </Button>
          </div>
        ) : (
          <>
            {/* Cart Items */}
            <div className="flex-1 overflow-y-auto space-y-4 py-4">
              {cartItems.map((item: any, index: number) => {
                const product = item.product;
                const currentPrice = product.salePrice ? parseFloat(product.salePrice) : parseFloat(product.price);
                const originalPrice = parseFloat(product.price);
                const discount = product.salePrice ? Math.round(((originalPrice - currentPrice) / originalPrice) * 100) : 0;

                return (
                  <div key={item.id} className="flex gap-3 p-3 bg-secondary/30 rounded-lg" data-testid={`cart-item-${index}`}>
                    <Link href={`/product/${product.id}`} onClick={() => onOpenChange(false)}>
                      <img
                        src={product.imageUrl}
                        alt={product.name}
                        className="w-16 h-16 object-cover rounded-lg"
                        data-testid={`cart-item-image-${index}`}
                      />
                    </Link>

                    <div className="flex-1 min-w-0">
                      <Link href={`/product/${product.id}`} onClick={() => onOpenChange(false)}>
                        <h4 className="font-medium text-foreground text-sm hover:text-primary transition-colors line-clamp-2" data-testid={`cart-item-name-${index}`}>
                          {product.name}
                        </h4>
                      </Link>
                      <p className="text-xs text-muted-foreground">{product.brand}</p>

                      <div className="flex flex-wrap gap-1 mt-1">
                        {item.size && (
                          <Badge variant="secondary" className="text-xs">Size: {item.size}</Badge>
                        )}
                        {item.color && (
                          <Badge variant="secondary" className="text-xs">Color: {item.color}</Badge>
                        )}
                      </div>

                      <div className="flex items-center justify-between mt-2">
                        <div className="flex items-center space-x-1">
                          <span className="font-medium text-sm" data-testid={`cart-item-price-${index}`}>
                            â‚¹{currentPrice.toLocaleString()}
                          </span>
                          {discount > 0 && (
                            <span className="text-xs text-muted-foreground line-through">
                              â‚¹{originalPrice.toLocaleString()}
                            </span>
                          )}
                        </div>

                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => removeItemMutation.mutate(item.id)}
                          disabled={removeItemMutation.isPending}
                          data-testid={`cart-item-remove-${index}`}
                        >
                          <Trash2 className="w-3 h-3" />
                        </Button>
                      </div>

                      <div className="flex items-center space-x-2 mt-2">
                        <Button
                          variant="outline"
                          size="sm"
                          className="h-6 w-6 p-0"
                          onClick={() => updateQuantityMutation.mutate({ id: item.id, quantity: Math.max(1, item.quantity - 1) })}
                          disabled={item.quantity <= 1 || updateQuantityMutation.isPending}
                          data-testid={`cart-item-decrease-${index}`}
                        >
                          <Minus className="w-3 h-3" />
                        </Button>
                        <span className="text-sm font-medium w-8 text-center" data-testid={`cart-item-quantity-${index}`}>
                          {item.quantity}
                        </span>
                        <Button
                          variant="outline"
                          size="sm"
                          className="h-6 w-6 p-0"
                          onClick={() => updateQuantityMutation.mutate({ id: item.id, quantity: item.quantity + 1 })}
                          disabled={updateQuantityMutation.isPending}
                          data-testid={`cart-item-increase-${index}`}
                        >
                          <Plus className="w-3 h-3" />
                        </Button>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>

            {/* Cart Summary */}
            <div className="border-t pt-4 space-y-4">
              <div className="space-y-2">
                <div className="flex justify-between text-sm">
                  <span className="text-muted-foreground">Subtotal</span>
                  <span data-testid="cart-subtotal">â‚¹{subtotal.toLocaleString()}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-muted-foreground">Shipping</span>
                  <span data-testid="cart-shipping">
                    {shipping === 0 ? "FREE" : `â‚¹${shipping}`}
                  </span>
                </div>

                {shipping > 0 && (
                  <p className="text-xs text-muted-foreground bg-secondary p-2 rounded">
                    Add â‚¹{(999 - subtotal).toLocaleString()} more for FREE shipping!
                  </p>
                )}

                <Separator />
                <div className="flex justify-between font-semibold">
                  <span>Total</span>
                  <span data-testid="cart-total">â‚¹{total.toLocaleString()}</span>
                </div>
              </div>

              <div className="space-y-2">
                <Link href="/checkout" onClick={() => onOpenChange(false)}>
                  <Button size="lg" className="w-full" data-testid="button-checkout-cart">
                    Checkout
                    <ArrowRight className="w-4 h-4 ml-2" />
                  </Button>
                </Link>
                <Link href="/cart" onClick={() => onOpenChange(false)}>
                  <Button variant="outline" size="lg" className="w-full" data-testid="button-view-cart">
                    View Cart
                  </Button>
                </Link>
              </div>
            </div>
          </>
        )}
      </SheetContent>
    </Sheet>
  );
}

client/src/components/layout/header.tsx.content:
import { useState } from "react";
import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import { 
  Search, 
  ShoppingBag, 
  Heart, 
  User, 
  Truck, 
  Menu,
  ChevronDown
} from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import type { CartItem, Product, WishlistItem } from "@shared/schema";
import CartSidebar from "@/components/cart/cart-sidebar";

export default function Header() {
  const [location] = useLocation();
  const { user, isAuthenticated } = useAuth();
  const [searchQuery, setSearchQuery] = useState("");
  const [showCart, setShowCart] = useState(false);
  const [showMobileMenu, setShowMobileMenu] = useState(false);

  const { data: cartItems = [] } = useQuery<(CartItem & { product: Product })[]>({
    queryKey: ["/api/cart"],
    enabled: isAuthenticated,
  });

  const { data: wishlistItems = [] } = useQuery<(WishlistItem & { product: Product })[]>({
    queryKey: ["/api/wishlist"], 
    enabled: isAuthenticated,
  });

  const cartItemCount = cartItems?.length || 0;
  const wishlistCount = wishlistItems?.length || 0;

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      window.location.href = `/products?search=${encodeURIComponent(searchQuery)}`;
    }
  };

  const categories = [
    { name: "Men", value: "men" },
    { name: "Women", value: "women" },
    { name: "Kids", value: "kids" },
    { name: "Accessories", value: "accessories" },
  ];

  return (
    <>
      {/* Top Banner */}
      <div className="bg-primary text-primary-foreground py-2 px-4 text-sm text-center">
        <span className="font-medium">Free Shipping on orders above â‚¹999 | Use code: FREESHIP</span>
      </div>

      {/* Main Header */}
      <header className="bg-card border-b border-border sticky top-0 z-50 shadow-sm">
        <div className="container mx-auto px-4">
          <div className="flex items-center justify-between h-16">
            {/* Logo */}
            <div className="flex items-center space-x-8">
              <Link href="/">
                <div className="flex items-center space-x-2" data-testid="link-logo">
                  <div className="bg-primary text-primary-foreground rounded-lg p-2">
                    <ShoppingBag className="w-5 h-5" />
                  </div>
                  <span className="text-2xl font-bold text-primary">StyleHub</span>
                </div>
              </Link>

              {/* Main Navigation */}
              <nav className="hidden lg:flex items-center space-x-8">
                {categories.map((category) => (
                  <DropdownMenu key={category.value}>
                    <DropdownMenuTrigger asChild>
                      <Button variant="ghost" className="flex items-center space-x-1" data-testid={`button-menu-${category.value}`}>
                        <span className="font-medium">{category.name}</span>
                        <ChevronDown className="w-3 h-3" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent>
                      <DropdownMenuItem asChild>
                        <Link href={`/products?category=${category.value}`}>
                          All {category.name}
                        </Link>
                      </DropdownMenuItem>
                      <DropdownMenuItem asChild>
                        <Link href={`/products?category=${category.value}&subcategory=shirts`}>
                          Shirts
                        </Link>
                      </DropdownMenuItem>
                      <DropdownMenuItem asChild>
                        <Link href={`/products?category=${category.value}&subcategory=pants`}>
                          Pants
                        </Link>
                      </DropdownMenuItem>
                      <DropdownMenuItem asChild>
                        <Link href={`/products?category=${category.value}&subcategory=shoes`}>
                          Shoes
                        </Link>
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                ))}
                <Link href="/products?sale=true">
                  <Button variant="ghost" className="font-medium text-accent hover:text-accent/80" data-testid="link-sale">
                    Sale
                  </Button>
                </Link>
              </nav>
            </div>

            {/* Search Bar */}
            <div className="flex-1 max-w-xl mx-8 hidden md:block">
              <form onSubmit={handleSearch} className="relative">
                <Input
                  type="text"
                  placeholder="Search for clothing, brands and more..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full bg-secondary pr-12"
                  data-testid="input-search"
                />
                <Button
                  type="submit"
                  variant="ghost"
                  size="sm"
                  className="absolute right-2 top-1/2 transform -translate-y-1/2"
                  data-testid="button-search"
                >
                  <Search className="w-4 h-4" />
                </Button>
              </form>
            </div>

            {/* Right Side Actions */}
            <div className="flex items-center space-x-6">
              {/* Track Order */}
              <Link href="/track">
                <Button variant="ghost" className="hidden lg:flex items-center space-x-2" data-testid="button-track">
                  <Truck className="w-5 h-5" />
                  <span className="font-medium">Track</span>
                </Button>
              </Link>

              {/* Account */}
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="ghost" className="flex items-center space-x-2" data-testid="button-account">
                    <User className="w-5 h-5" />
                    <span className="hidden lg:inline font-medium">
                      {isAuthenticated ? user?.firstName || 'Account' : 'Account'}
                    </span>
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end">
                  {isAuthenticated ? (
                    <>
                      <DropdownMenuItem asChild>
                        <Link href="/orders">My Orders</Link>
                      </DropdownMenuItem>
                      <DropdownMenuItem asChild>
                        <Link href="/profile">Profile</Link>
                      </DropdownMenuItem>
                      {user?.isAdmin && (
                        <DropdownMenuItem asChild>
                          <Link href="/admin">Admin Panel</Link>
                        </DropdownMenuItem>
                      )}
                      <DropdownMenuItem>
                        <a href="/api/logout">Logout</a>
                      </DropdownMenuItem>
                    </>
                  ) : (
                    <DropdownMenuItem>
                      <a href="/api/login">Login</a>
                    </DropdownMenuItem>
                  )}
                </DropdownMenuContent>
              </DropdownMenu>

              {/* Wishlist */}
              {isAuthenticated && (
                <Button variant="ghost" className="relative" data-testid="button-wishlist">
                  <Heart className="w-5 h-5" />
                  {wishlistCount > 0 && (
                    <Badge variant="destructive" className="absolute -top-2 -right-2 h-5 w-5 flex items-center justify-center text-xs">
                      {wishlistCount}
                    </Badge>
                  )}
                </Button>
              )}

              {/* Cart */}
              <Button 
                variant="ghost" 
                className="relative" 
                onClick={() => setShowCart(true)}
                data-testid="button-cart"
              >
                <ShoppingBag className="w-5 h-5" />
                {cartItemCount > 0 && (
                  <Badge variant="destructive" className="absolute -top-2 -right-2 h-5 w-5 flex items-center justify-center text-xs">
                    {cartItemCount}
                  </Badge>
                )}
              </Button>

              {/* Mobile Menu Toggle */}
              <Sheet open={showMobileMenu} onOpenChange={setShowMobileMenu}>
                <SheetTrigger asChild>
                  <Button variant="ghost" className="lg:hidden" data-testid="button-mobile-menu">
                    <Menu className="w-5 h-5" />
                  </Button>
                </SheetTrigger>
                <SheetContent side="right" className="w-full max-w-sm">
                  <div className="space-y-6">
                    {/* Mobile Search */}
                    <form onSubmit={handleSearch} className="relative">
                      <Input
                        type="text"
                        placeholder="Search..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="pr-12"
                        data-testid="input-mobile-search"
                      />
                      <Button
                        type="submit"
                        variant="ghost"
                        size="sm"
                        className="absolute right-2 top-1/2 transform -translate-y-1/2"
                      >
                        <Search className="w-4 h-4" />
                      </Button>
                    </form>

                    {/* Mobile Navigation */}
                    <nav className="space-y-4">
                      {categories.map((category) => (
                        <Link 
                          key={category.value} 
                          href={`/products?category=${category.value}`}
                          onClick={() => setShowMobileMenu(false)}
                        >
                          <Button variant="ghost" className="w-full justify-start text-lg" data-testid={`link-mobile-${category.value}`}>
                            {category.name}
                          </Button>
                        </Link>
                      ))}
                      <Link href="/products?sale=true" onClick={() => setShowMobileMenu(false)}>
                        <Button variant="ghost" className="w-full justify-start text-lg text-accent">
                          Sale
                        </Button>
                      </Link>
                      <Link href="/track" onClick={() => setShowMobileMenu(false)}>
                        <Button variant="ghost" className="w-full justify-start text-lg">
                          Track Order
                        </Button>
                      </Link>
                    </nav>
                  </div>
                </SheetContent>
              </Sheet>
            </div>
          </div>
        </div>
      </header>

      {/* Cart Sidebar */}
      <CartSidebar open={showCart} onOpenChange={setShowCart} />
    </>
  );
}

client/src/components/order/order-tracki.content:
import { useQuery } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Card, CardContent } from "@/components/ui/card";
import { 
  Package, 
  Truck, 
  CheckCircle, 
  Clock, 
  MapPin,
  AlertCircle
} from "lucide-react";

interface OrderTrackingProps {
  orderId: string;
  onClose: () => void;
}

export default function OrderTracking({ orderId, onClose }: OrderTrackingProps) {
  const { data: order, isLoading, error } = useQuery({
    queryKey: ["/api/orders", orderId],
    enabled: !!orderId,
  });

  const getStatusIcon = (status: string, isActive: boolean) => {
    const iconClass = `w-5 h-5 ${isActive ? 'text-primary' : 'text-muted-foreground'}`;

    switch (status) {
      case "pending":
      case "confirmed":
        return <Clock className={iconClass} />;
      case "shipped":
        return <Package className={iconClass} />;
      case "out_for_delivery":
        return <Truck className={iconClass} />;
      case "delivered":
        return <CheckCircle className={iconClass} />;
      default:
        return <Clock className={iconClass} />;
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case "pending":
        return "Order Placed";
      case "confirmed":
        return "Order Confirmed";
      case "shipped":
        return "Shipped";
      case "out_for_delivery":
        return "Out for Delivery";
      case "delivered":
        return "Delivered";
      default:
        return status;
    }
  };

  const trackingSteps = [
    { status: "pending", label: "Order Placed" },
    { status: "confirmed", label: "Order Confirmed" },
    { status: "shipped", label: "Shipped" },
    { status: "out_for_delivery", label: "Out for Delivery" },
    { status: "delivered", label: "Delivered" },
  ];

  const getCurrentStepIndex = (currentStatus: string) => {
    return trackingSteps.findIndex(step => step.status === currentStatus);
  };

  return (
    <Dialog open={!!orderId} onOpenChange={() => onClose()}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Package className="w-5 h-5" />
            <span>Order Tracking</span>
          </DialogTitle>
        </DialogHeader>

        {isLoading && (
          <div className="flex justify-center py-8">
            <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
          </div>
        )}

        {error && (
          <Card>
            <CardContent className="p-8 text-center">
              <AlertCircle className="w-12 h-12 text-destructive mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-foreground mb-2">Order Not Found</h3>
              <p className="text-muted-foreground">
                We couldn't find the details for this order. Please try again later.
              </p>
            </CardContent>
          </Card>
        )}

        {order && !isLoading && (
          <div className="space-y-6">
            {/* Order Header */}
            <Card>
              <CardContent className="p-6">
                <div className="flex items-center justify-between mb-4">
                  <div>
                    <h3 className="text-xl font-semibold text-foreground">Order #{order.orderNumber}</h3>
                    <p className="text-muted-foreground">
                      Placed on {new Date(order.createdAt).toLocaleDateString()}
                    </p>
                  </div>
                  <Badge 
                    variant="secondary"
                    className="text-sm px-3 py-1"
                    data-testid="order-status-badge"
                  >
                    {getStatusLabel(order.status)}
                  </Badge>
                </div>

                <div className="text-sm text-muted-foreground">
                  <p>Total Amount: <span className="font-medium text-foreground">â‚¹{parseFloat(order.totalAmount).toLocaleString()}</span></p>
                  {order.estimatedDelivery && (
                    <p>Expected Delivery: <span className="font-medium text-foreground">{new Date(order.estimatedDelivery).toLocaleDateString()}</span></p>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Tracking Progress */}
            <Card>
              <CardContent className="p-6">
                <h4 className="font-semibold text-foreground mb-6">Tracking Progress</h4>

                <div className="space-y-4">
                  {/* Progress Steps */}
                  <div className="flex justify-between relative">
                    <div className="absolute top-5 left-5 right-5 h-0.5 bg-border" />
                    <div 
                      className="absolute top-5 left-5 h-0.5 bg-primary transition-all duration-500"
                      style={{ 
                        width: `${(getCurrentStepIndex(order.status) / (trackingSteps.length - 1)) * 100}%` 
                      }}
                    />

                    {trackingSteps.map((step, index) => {
                      const isActive = getCurrentStepIndex(order.status) >= index;
                      const isCurrent = order.status === step.status;

                      return (
                        <div key={step.status} className="flex flex-col items-center relative z-10">
                          <div 
                            className={`w-10 h-10 rounded-full border-2 flex items-center justify-center transition-colors ${
                              isActive 
                                ? 'bg-primary border-primary text-primary-foreground' 
                                : 'bg-background border-border'
                            }`}
                            data-testid={`tracking-step-${step.status}`}
                          >
                            {getStatusIcon(step.status, isActive)}
                          </div>
                          <p className={`mt-2 text-xs font-medium text-center ${isActive ? 'text-foreground' : 'text-muted-foreground'}`}>
                            {step.label}
                          </p>
                          {isCurrent && (
                            <p className="text-xs text-primary font-medium mt-1">Current</p>
                          )}
                        </div>
                      );
                    })}
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Tracking Events */}
            {order.tracking && order.tracking.length > 0 && (
              <Card>
                <CardContent className="p-6">
                  <h4 className="font-semibold text-foreground mb-4">Tracking History</h4>
                  <div className="space-y-4">
                    {order.tracking.map((event: any, index: number) => (
                      <div key={event.id} className="flex space-x-4" data-testid={`tracking-event-${index}`}>
                        <div className="flex-shrink-0">
                          <div className="w-8 h-8 bg-primary/10 rounded-full flex items-center justify-center">
                            {getStatusIcon(event.status, true)}
                          </div>
                        </div>
                        <div className="flex-1">
                          <div className="flex items-center justify-between">
                            <h5 className="font-medium text-foreground">
                              {getStatusLabel(event.status)}
                            </h5>
                            <p className="text-sm text-muted-foreground">
                              {new Date(event.timestamp).toLocaleString()}
                            </p>
                          </div>
                          {event.message && (
                            <p className="text-sm text-muted-foreground mt-1">{event.message}</p>
                          )}
                          {event.location && (
                            <div className="flex items-center space-x-1 mt-1">
                              <MapPin className="w-3 h-3 text-muted-foreground" />
                              <p className="text-xs text-muted-foreground">{event.location}</p>
                            </div>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Order Items */}
            {order.orderItems && order.orderItems.length > 0 && (
              <Card>
                <CardContent className="p-6">
                  <h4 className="font-semibold text-foreground mb-4">
                    Items in this order ({order.orderItems.length})
                  </h4>
                  <div className="space-y-4">
                    {order.orderItems.map((item: any, index: number) => (
                      <div key={item.id} className="flex space-x-4" data-testid={`order-item-${index}`}>
                        <img
                          src={item.product.imageUrl}
                          alt={item.product.name}
                          className="w-16 h-16 object-cover rounded-lg"
                        />
                        <div className="flex-1">
                          <h5 className="font-medium text-foreground">{item.product.name}</h5>
                          <p className="text-sm text-muted-foreground">{item.product.brand}</p>
                          <div className="flex items-center space-x-4 mt-1">
                            {item.size && (
                              <span className="text-xs bg-secondary px-2 py-1 rounded">
                                Size: {item.size}
                              </span>
                            )}
                            {item.color && (
                              <span className="text-xs bg-secondary px-2 py-1 rounded">
                                Color: {item.color}
                              </span>
                            )}
                            <span className="text-sm text-muted-foreground">Qty: {item.quantity}</span>
                            <span className="font-medium">â‚¹{parseFloat(item.price).toLocaleString()}</span>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Shipping Address */}
            {order.shippingAddress && (
              <Card>
                <CardContent className="p-6">
                  <h4 className="font-semibold text-foreground mb-4">Delivery Address</h4>
                  <div className="text-sm text-muted-foreground space-y-1">
                    <p className="font-medium text-foreground">
                      {order.shippingAddress.firstName} {order.shippingAddress.lastName}
                    </p>
                    <p>{order.shippingAddress.address}</p>
                    <p>{order.shippingAddress.city}, {order.shippingAddress.state}</p>
                    <p>{order.shippingAddress.pincode}</p>
                    <p>{order.shippingAddress.phone}</p>
                  </div>
                </CardContent>
              </Card>
            )}
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/product/product-ca.content:
import { useState } from "react";
import { Link } from "wouter";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { Heart, Star, ShoppingCart } from "lucide-react";

interface ProductCardProps {
  product: any;
  viewMode?: "grid" | "list";
  className?: string;
}

export default function ProductCard({ product, viewMode = "grid", className }: ProductCardProps) {
  const { isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isHovered, setIsHovered] = useState(false);

  const currentPrice = product.salePrice ? parseFloat(product.salePrice) : parseFloat(product.price);
  const originalPrice = parseFloat(product.price);
  const discount = product.salePrice ? Math.round(((originalPrice - currentPrice) / originalPrice) * 100) : 0;

  const addToCartMutation = useMutation({
    mutationFn: async () => {
      return apiRequest("POST", "/api/cart", {
        productId: product.id,
        quantity: 1,
      });
    },
    onSuccess: () => {
      toast({
        title: "Added to Cart",
        description: "Product has been added to your cart successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Please Sign In",
          description: "You need to sign in to add items to cart.",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 1000);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add item to cart.",
        variant: "destructive",
      });
    },
  });

  const toggleWishlistMutation = useMutation({
    mutationFn: async () => {
      // For simplicity, always try to add to wishlist
      return apiRequest("POST", "/api/wishlist", { productId: product.id });
    },
    onSuccess: () => {
      toast({
        title: "Added to Wishlist",
        description: "Product has been added to your wishlist.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/wishlist"] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Please Sign In",
          description: "You need to sign in to add items to wishlist.",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 1000);
        return;
      }
      // If it fails, it might already be in wishlist, so try to remove
      apiRequest("DELETE", `/api/wishlist/${product.id}`)
        .then(() => {
          toast({
            title: "Removed from Wishlist",
            description: "Product has been removed from your wishlist.",
          });
          queryClient.invalidateQueries({ queryKey: ["/api/wishlist"] });
        })
        .catch(() => {
          toast({
            title: "Error",
            description: "Failed to update wishlist.",
            variant: "destructive",
          });
        });
    },
  });

  if (viewMode === "list") {
    return (
      <Card className={`hover:shadow-lg transition-shadow ${className}`} data-testid="card-product-list">
        <CardContent className="p-6">
          <div className="flex gap-6">
            <Link href={`/product/${product.id}`}>
              <img
                src={product.imageUrl}
                alt={product.name}
                className="w-32 h-32 object-cover rounded-lg"
                data-testid="img-product-list"
              />
            </Link>

            <div className="flex-1 space-y-3">
              <div className="flex justify-between items-start">
                <div>
                  <Link href={`/product/${product.id}`}>
                    <h3 className="text-lg font-semibold text-foreground hover:text-primary transition-colors" data-testid="text-product-name-list">
                      {product.name}
                    </h3>
                  </Link>
                  <p className="text-sm text-muted-foreground">{product.brand}</p>
                  {product.description && (
                    <p className="text-sm text-muted-foreground mt-2 line-clamp-2">{product.description}</p>
                  )}
                </div>

                <div className="text-right">
                  <div className="flex items-center space-x-2 mb-2">
                    <span className="text-xl font-bold text-foreground" data-testid="text-product-price-list">
                      â‚¹{currentPrice.toLocaleString()}
                    </span>
                    {discount > 0 && (
                      <>
                        <span className="text-sm text-muted-foreground line-through">
                          â‚¹{originalPrice.toLocaleString()}
                        </span>
                        <Badge variant="destructive" className="text-xs">{discount}% OFF</Badge>
                      </>
                    )}
                  </div>

                  <div className="flex items-center space-x-1 mb-3">
                    <Star className="w-4 h-4 text-yellow-400 fill-current" />
                    <span className="text-sm text-muted-foreground">{product.rating || "0"} ({product.reviewCount || 0})</span>
                  </div>
                </div>
              </div>

              <div className="flex items-center justify-between">
                <div className="flex flex-wrap gap-2">
                  {product.isFeatured && <Badge>Featured</Badge>}
                  {product.tags?.slice(0, 2).map((tag: string) => (
                    <Badge key={tag} variant="secondary" className="text-xs">{tag}</Badge>
                  ))}
                </div>

                <div className="flex space-x-2">
                  {isAuthenticated && (
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => toggleWishlistMutation.mutate()}
                      disabled={toggleWishlistMutation.isPending}
                      data-testid="button-wishlist-list"
                    >
                      <Heart className="w-4 h-4" />
                    </Button>
                  )}
                  <Button
                    size="sm"
                    onClick={() => addToCartMutation.mutate()}
                    disabled={addToCartMutation.isPending}
                    data-testid="button-add-to-cart-list"
                  >
                    <ShoppingCart className="w-4 h-4 mr-2" />
                    {addToCartMutation.isPending ? "Adding..." : "Add to Cart"}
                  </Button>
                </div>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card 
      className={`group cursor-pointer hover:shadow-lg transition-all duration-300 ${className}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      data-testid="card-product-grid"
    >
      <CardContent className="p-0">
        <div className="relative">
          <Link href={`/product/${product.id}`}>
            <img
              src={product.imageUrl}
              alt={product.name}
              className="w-full h-64 object-cover rounded-t-2xl group-hover:scale-105 transition-transform duration-300"
              data-testid="img-product-grid"
            />
          </Link>

          {/* Overlay badges */}
          <div className="absolute top-3 left-3 space-y-2">
            {discount > 0 && (
              <Badge variant="destructive" className="text-xs font-medium">
                -{discount}%
              </Badge>
            )}
            {product.isFeatured && (
              <Badge className="text-xs font-medium">
                Featured
              </Badge>
            )}
            {product.tags?.includes("new") && (
              <Badge variant="secondary" className="text-xs font-medium">
                New
              </Badge>
            )}
          </div>

          {/* Wishlist button */}
          {isAuthenticated && (
            <Button
              variant="ghost"
              size="sm"
              className={`absolute top-3 right-3 bg-card/80 backdrop-blur-sm hover:bg-card transition-all duration-200 ${
                isHovered ? 'opacity-100' : 'opacity-0'
              }`}
              onClick={(e) => {
                e.preventDefault();
                toggleWishlistMutation.mutate();
              }}
              disabled={toggleWishlistMutation.isPending}
              data-testid="button-wishlist-grid"
            >
              <Heart className="w-4 h-4" />
            </Button>
          )}
        </div>

        <div className="p-4">
          <Link href={`/product/${product.id}`}>
            <h3 className="font-semibold text-foreground mb-2 group-hover:text-primary transition-colors line-clamp-2" data-testid="text-product-name-grid">
              {product.name}
            </h3>
          </Link>
          <p className="text-sm text-muted-foreground mb-3">{product.brand}</p>

          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-2">
              <span className="text-lg font-bold text-foreground" data-testid="text-product-price-grid">
                â‚¹{currentPrice.toLocaleString()}
